import React, { Component, useState } from "react";
import { Container, Row, Col } from "react-bootstrap";

export class ExtraInfo extends Component {
  
     

        onTrigger = (event) => {
            this.props.parentCallback(event.target.myname);
            event.preventDefault();
        }
        render(){
            const inputStyle = {
                fontFamily: "Arial Black",
                padding: "10px",
            }
        return (
            <div>
                <Container fluid="md" className="container">
                    <Row>
                        <Col>
                            
                                <label style={inputStyle}>Extra Information:</label>
                            
                        </Col>
                        <Col>
                            
                        <input onChange={this.onTrigger} id="extrainfo"  type="text-area" name="myname" required ></input>
                        </Col>
                    </Row>
                </Container>
                <hr></hr>
            </div>
        )
    }
}
